SELECT * FROM sys.dm_external_data_processed;

SELECT * FROM sys.configurations
WHERE name LIKE 'Data Processed %';

sp_set_data_processed_limit
    @type = N'Monthly',
    @limit_tb = 2;

sp_set_data_processed_limit
    @type = N'Weekly',
    @limit_tb = 1;

sp_set_data_processed_limit
    @type = N'Daily',
    @limit_tb = 1;
